import React from 'react'
import styled from 'styled-components'

export default function Login() {
    return (
        <Container>
            <CTA>
                <CTALogoOne src="/images/cta-logo-one.svg" />
                <SignUp>GET ALL THERE</SignUp>
                <Description>Get Premier Access to Raya and the Last Dragon for an additional fee with a Disney+ subscription. As of 03/26/21, the price of Disney+ and The Disney Bundle will increase by $1.</Description>
                <CTALogoTwo src="/images/cta-logo-two.svg" />
            </CTA>

        </Container>
    )
}
const Container = styled.div`
    position: relative;
    height: calc(100vh - 70px); 
    display: flex;
    align-items: center;
    justify-content: center;

    &:before{
        background-position: top;
        background-size: cover;
        background-repeat: no-repeat;
        background: url("/images/login-background.jpg");
        content: "";
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        z-index: -1;

    }


`

const CTA = styled.div`
    max-width: 65px;
    padding: 80px 40px;
    width: 80%;
    display: flex;
    flex-direction: column;

`

const CTALogoOne = styled.img``
const CTALogoTwo = styled.img``

const SignUp = styled.a`
width: 100%;
background-color: #0063e5;
font-weight: bold;
padding: 17px 0;
color: #f9f9f9;
border-radius: 4px;
text-align: center;
font-size: 18px;
cursor: pointer;
transition: all 250ms;
letter-spacing: 1.5px;
margin-top: 8px;
margin-bottom: 12px;

&:hover{
    background: #0483ee;
}
`

const Description = styled.p`
font-size: 10px;
letter-spacing: 1.5px;
text-align: center;
line-height: 1.5;

`
